        axios.get(`https://newsapi.org/v2/top-headlines?country=us&apiKey=d4eeb79ada9e4603b2870fb49b15fd30`).then(data => setNews(data.data.articles));
    },[])
  return (
   <>
    <Header setSearch={setSearch}/>      
