import React, { useState } from 'react'
import NewsImg from "../images/d-world-news-background-digital-breaking-studio-report-live-208423108.jpg";
import SearchIcon from '@material-ui/icons/Search';
import axios from 'axios';
const Main = ({ news }) => {

  const API_KEY = "d4eeb79ada9e4603b2870fb49b15fd30";
    const [ search , setSearch ] = useState("");
    const [searchNews, setSearchNews ] = useState("");

    axios.get(`https://newsapi.org/v2/everything?q=&from=2021-10-07&sortBy=popularity&apiKey=${API_KEY}`).then(data => {
        console.log(data)
    })

    if(!news) return "Loading...";
     console.log(news.map(item => item.urlToImg))
    return (
        <>
        <div className="news-grid">
        {news.map((item) => (
           
               <div className="news-card">
                   <img src={item?.urlToImage || NewsImg} className="news-image" />
                   <h3> Source:- {item.source.name}</h3>
                   <p>Author:- {item.author}</p>
                   <p>Title:- {item.title}</p>
                   <a href={item?.url} className="news-btn">Read Full News..</a>
                   <p>{item.publishedAt}</p>
                   </div>
               
        ))}
        </div>
        <hr />
        <div>
            <SearchIcon />
            <input placeholder="search about news" onChange={(e) => setSearch(e.target.value)}  />
        </div>
        </>
    )
}

export default Main
